import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ViewUserGrid.css';

function ViewUserGrid() {
  const [userData, setUserData] = useState([ {
    id: 'ums001',
    name: 'sandhya',
    email: 'abc@gmail.com',
    contactNo: '2342342342',
    address: 'Kodambakkam',
      role:'user',
    },
    {
      id: 'ums002',
    name: 'Meghana',
    email: 'abc@gmail.com',
    contactNo: '2342342342',
    address: 'Kodambakkam',
      role:'admin',
    }

    ,
    {
      id: 'ums003',
      name: 'reeta',
      email: 'abc@gmail.com',
      contactNo: '2342342342',
      address: 'Kodambakkam',
        role:'user',
      },
      {
        id: 'ums004',
        name: 'soorya narayanan',
        email: 'abc@gmail.com',
        contactNo: '2342342342',
        address: 'Kodambakkam',
        role:'user',
        
        },
        {
          id: 'ums005',
        name: 'Arshad Ahamed',
        email: 'abc@gmail.com',
        contactNo: '2342342342',
        address: 'Kodambakkam',
          role:'admin',
        }]);
  const [filteredData, setFilteredData] = useState([]);
  const [filterUsername, setFilterUsername] = useState('');
  const [filterRole, setFilterRole] = useState('');
  
    useEffect(() => {
      // Fetch user data from the backend API
      axios.get('https://your-backend-api-url/users')
        .then((response) => {
          const data = response.data;
          setUserData(data);
          setFilteredData(data);
        })
        .catch((error) => {
          console.error('Error fetching user data:', error);
        });
    }, []);
  
    useEffect(() => {
      // Filter the data based on the username and role
      const filtered = userData.filter((user) => {
        return (
          user.name.toLowerCase().includes(filterUsername.toLowerCase()) &&
          (filterRole === '' || user.role === filterRole)
        );
      });
  
      setFilteredData(filtered);
    }, [filterUsername, filterRole, userData]);
  
    return (
      <div className="user-grid-container">
        <h2>User List</h2>
        <div className="filter-options">
          <label>
            Filter by Username:
            <input
              type="text"
              value={filterUsername}
              onChange={(e) => setFilterUsername(e.target.value)}
            />
          </label>
          <label>
            Filter by Role:
            <select value={filterRole} onChange={(e) => setFilterRole(e.target.value)}>
              <option value="">All Roles</option>
              <option value="user">User</option>
              <option value="admin">Admin</option>
            </select>
          </label>
        </div>
        <div className="user-grid">
          {filteredData.map((user) => (
            <div key={user.id} className={`user-card ${user.role.toLowerCase()}`}>
              <h3>Name: {user.name}</h3>
              <p>Email: {user.email}</p>
              <p>User ID: {user.id}</p>
              <p>Role: {user.role}</p>
              <p>Contact No: {user.contactNo}</p>
              <p>Address: {user.address}</p>
              {/* Add more user details as needed */}
            </div>
          ))}
        </div>
      </div>
    );
  }
  
  export default ViewUserGrid;
